import { Component, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { CartService } from '../services';
import { getAddress } from '../utils/utils';

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css']
})
export class CheckoutComponent {
  data!: BehaviorSubject<any[]>;
  addresses!: any[];

  constructor(private cartService: CartService) {
  }

  ngOnInit() {
    this.data = this.cartService.getCart();

    this.addresses = getAddress();
  }

  removeItem(item: any) {
    this.cartService.remove(item);
  }

  onSelect(item: any, event: any) {
    console.log(event.target.checked);
    this.cartService.select(item, event.target.checked);
  }

  selectAll() {
    this.cartService.selectAll(true);
  }

  deselectAll() {
    this.cartService.selectAll(false);
  }

  showSelectAll(items: any[]) {
    return items.some(x => !x.selected);
  }
}
